mapper.select:
select count(*)
from CLIENT C join (select q.qcode, q.client_id, q.subject, q.contents, q.a_contents, q.q_date, q.a_date, q.status, A.lname, A.lcode from LESSON_QNA q join (select lcode, lname from LESSON where TEACHER_NAME in
#{name}
) A on A.lcode = q.lcode
where q.q_date between TO_DATE(#{fromDate}, 'yyyy-mm-dd') and TO_DATE(#{toDate}, 'yyyy-mm-dd') + 0.99999
) B on B.client_id = C.client_id

select T.* from ( select B.qcode, B.lcode, B.lname, B.client_id, C.name, B.subject, B.contents, B.a_contents, to_char(B.q_date,'yyyy-mm-dd') q_date, B.a_date, B.status, row_number() over(order by q_date desc) r_num
from CLIENT C join (select q.qcode, q.client_id, q.subject, q.contents, q.a_contents, q.q_date, q.a_date, q.status, A.lname, A.lcode from LESSON_QNA q join (select lcode, lname from LESSON where TEACHER_NAME in
#{name}
) A on A.lcode = q.lcode
where q.q_date between TO_DATE(#{fromDate}, 'yyyy-mm-dd') and TO_DATE(#{toDate}, 'yyyy-mm-dd') + 0.99999
) B on B.client_id = C.client_id
order by q_date desc ) T where r_num between #{lpvo.startNum} and #{lpvo.endNum}

select count(*) from CLIENT C join (select q.qcode, q.client_id, q.subject, q.contents, q.a_contents, q.q_date, q.a_date, q.status, A.lname, A.lcode from LESSON_QNA q join (select lcode, lname from LESSON where TEACHER_NAME = #{tn_list} ) A on A.lcode = q.lcode
where q_date between #{fromDate} and #{toDate}
) B on B.client_id = C.client_id

mapper.update:
update LESSON_QNA
set A_CONTENTS = TO_CLOB(#{aContents}), a_date = sysdate 
where QCODE = #{qcode}
