mapper.select:
select		lcode
from		students
where	status='C' and client_id=#{clientId}

select		subject
from		review
where	client_ID=#{clientId} and lcode=#{lcode}

select		lcode
from		lesson_like
where	client_id=#{clientId} and lcode=#{lcode}

select lcode
from students
where client_id=#{clientId}

select		q.q_date, l.lname, q.status
from		lesson_qna q, lesson l
where	l.lcode=#{lcode} and q.lcode=#{lcode} and q.client_id=#{clientId}

select		lcode
from		lesson_qna
where	client_id=#{clientId}

select		r.r_date, l.lname, r.status
from		report r, lesson l
where	l.lcode=#{lcode} and r.lcode=#{lcode} and r.client_id=#{clientId}

select		lcode
from		report
where	client_id=#{clientId}

 	select count(lcode) cnt
	from students
	where client_id=#{clientId}

 	select count(lcode) cnt
	from students
	where client_id=#{clientId} and status=#{status}
